#!/bin/bash
# This code is generated by OpenAI's GPT-4o model.

# Create the dataset directory if it doesn't exist
mkdir -p ./fluid_data_gen/new_dataset

cd ./fluid_data_gen/new_dataset

# List of file names to download (replace with actual file names from Zenodo)
FILES=(
  "cs1list.zip"
  "cs1list1.zip"
  "cs2list.zip"
  "cs2list1.zip"
  "datalist.zip"
  "datalist1.zip"
  "hirtorylist.zip"
  "historylist1.zip"
  "templist.zip"
  "templist1.zip"
)

# Base URL for Zenodo record
BASE_URL="https://zenodo.org/record/15271036/files"

# Download and unzip each file
for FILE in "${FILES[@]}"; do
  echo "Downloading $FILE..."
  wget -O "$FILE" "$BASE_URL/$FILE?download=1"

  echo "Unzipping $FILE..."
  unzip -o "$FILE"
  rm "$FILE"
done

echo "All files downloaded and extracted successfully."
